import React from 'react';
import { Container } from './styled/Container.styled';
import { Footericon, Huddlefootericon, Huddleleftpage, Huddlelogo, Huddlemockups, Huddlerightpage, Registerbutton, Styledhuddlelandingpage } from './styled/Landingpage.styled';
import {
  faFacebook,
  faTwitter,
  faInstagram
} from "@fortawesome/free-brands-svg-icons";
import logo from './images/logo.svg';
import mockups from './images/illustration-mockups.svg';

const socials = [
  {
    icon: faFacebook,
    url: "https://www.facebook.com",
  },
  {
    icon: faTwitter,
    url: "https://www.twitter.com",
  },
  {
    icon: faInstagram,
    url: "https://www.instagram.com/fays.dalgona",
  }
];

export const Huddlelandingpage = () => {
  return (
    <Container>
        <Styledhuddlelandingpage>
          <Huddleleftpage>
            <Huddlelogo src={logo} alt='logo'/>
            <Huddlemockups src={mockups} alt='mockups'/>
          </Huddleleftpage>
          <Huddlerightpage>
            <h1>
              Build The Community Your Fans Will Love
            </h1>
            <p>
              Huddle re-imagines the way we build communities. You have a voice, but so does your audience. 
              Create connections with your users as you engage in genuine discussion.
            </p>
            <Registerbutton>
              Register
            </Registerbutton>
            <Huddlefootericon>
              {socials.map(({icon,url})=>(
                <a key={url} href={url}>
                  <Footericon src={icon} key={url} />
                </a>
              ))}
              {/* <a href='www.facebook.com'><Footericon src={fbicon} alt='fbicon' width={15}/></a>
              <Footericon src={twiticon} alt='twiticon' width={25}/>
              <Footericon src={igicon} alt='igicon' width={25}/> */}
            </Huddlefootericon>
          </Huddlerightpage>
        </Styledhuddlelandingpage>
    </Container>
  )
}
