import styled from "styled-components";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import logo from '../images/logo.svg';
import mockups from '../images/illustration-mockups.svg';

export const Styledhuddlelandingpage = styled.div`
    display: flex;
    flex-direction:row;
    margin:0;
`
export const Huddleleftpage = styled(Styledhuddlelandingpage)`
    flex-direction: column;
    margin:30px 30px;
`
export const Huddlelogo = styled.img`
    display: flex;
    width: 200px;
    margin:0 40px 20px;
`
export const Huddlemockups = styled(Huddlelogo)`
    width: 680px;
    margin:40px 40px 0;
`
export const Huddlerightpage = styled(Styledhuddlelandingpage)`
    flex-direction: column;
    margin:40px 80px 10px 10px;
    justify-content: center;
`
export const Registerbutton = styled.button`
    font-family:var(--Primary-font-style);
    color:var(--Violet);
    border-radius: 50px;
    width: 170px;
    height:40px;
    margin:10px 0;
    box-shadow: 0 5px 5px 5px rgba(0,0,0,0.2);
    border-style:none;
    &:hover{
        color:#fff;
        background-color: var(--Soft-Magenta);
        cursor: pointer;
    }
`
export const Huddlefootericon = styled(Styledhuddlelandingpage)`
    justify-content: flex-end;
    gap:15px;
    padding: 10px;
    border-radius:50%;
    margin:160px 30px 0;
`
export const Footericon = styled(FontAwesomeIcon)`
    border-radius: 50%;
    color:#fff;
    border: 1px solid #fff;
    padding: 5px;
    font-size: 20px;
    &:hover{
        color:var(--Soft-Magenta);
        border-color: var(--Soft-Magenta);
        
        cursor: pointer;
    }
`
