import styled from "styled-components";
import logo from '../images/logo.svg';
import mockups from '../images/illustration-mockups.svg';

export const Styledhuddlelandingpage = styled.article`
    display: flex;
    flex-direction:row;
    margin:0;
`
export const Huddleleftpage = styled(Styledhuddlelandingpage)`
    flex-direction: column;
    margin:30px 0;
`
export const Huddlelogo = styled.img`
    display: flex;
    width: 200px;
    margin:0 40px 20px;
`
export const Huddlemockups = styled(Huddlelogo)`
    width: 680px;
    margin:40px 40px 0;
`
export const Huddlerightpage = styled(Styledhuddlelandingpage)`
    flex-direction: column;
    margin:40px 40px;
    justify-content: center;
`
export const Registerbutton = styled.button`
    border-radius: 50px;
    box-shadow: 0 10px 10px 5px rgba(0,0,0,0.2);
`
