import styled from "styled-components";

export const Styledhuddlelandingpage = styled.article`
    display: flex;
    flex-direction:row;
    margin:30px 40px;
`
export const Huddleleftpage = styled.div`
    display: flex;
    flex-direction: column;
    margin:0;
`
export const Huddlerightpage = styled.div`
    display: flex;
    flex-direction: column;
    margin:0;
`
